-- DATABASE DESIGN AND CREATION --

-- Remove the existing PlayPixel database if it exists, to start fresh
DROP DATABASE IF EXISTS PlayPixel;

-- Create a new database named PlayPixel for the business process
CREATE DATABASE IF NOT EXISTS PlayPixel;

-- Select the PlayPixel database for subsequent operations
USE PlayPixel;

-- Create Log table
-- This table will record events occurring within the database for auditing and tracking
DROP TABLE IF EXISTS Log;
CREATE TABLE Log (
    LogID INT PRIMARY KEY AUTO_INCREMENT, -- Unique identifier for each log entry
    EventDescription TEXT, -- Description of the event being logged
    EventDate DATETIME -- The date and time when the event occurred
);


-- Creating a stored procedure named LogEvent to insert log entries
-- This improves code reusability and encapsulates the logging logic
DELIMITER //
CREATE PROCEDURE LogEvent(IN eventDescription VARCHAR(1000))
BEGIN
    -- Inserts a new log entry with the provided description and the current timestamp
    VALUES (eventDescription, NOW());
END;
//
DELIMITER ;


-- Call the LogEvent stored procedure to log the creation of the Log table
-- The provided event description includes the SQL code used to create the Log table
CALL LogEvent('DROP TABLE IF EXISTS Log;
    CREATE TABLE Log (
    LogID INT PRIMARY KEY AUTO_INCREMENT,
    EventDescription VARCHAR(1000),
    EventDate DATETIME
);');
-- Call the LogEvent stored procedure to log the successful creation of the Log table
CALL LogEvent('Log Table Created!');

-- Create the ZipCodes table
-- This table stores information about Zip Codes, including their state and country.
DROP TABLE IF EXISTS ZipCode;
CREATE TABLE ZipCode (
    ZipCode VARCHAR(20) PRIMARY KEY,
    State VARCHAR(50),
    Country VARCHAR(50)
);
CALL LogEvent('ZipCode Table Creation');
CALL LogEvent('ZipCode Table Created!');

-- Create the Addresses table
-- This table stores addresses and has a unique Zip Code constraint.
DROP TABLE IF EXISTS Address;
CREATE TABLE Address (
    AddressID INT,
    Street VARCHAR(100),
    City VARCHAR(50),
    ZipCode VARCHAR(20) UNIQUE,
    PRIMARY KEY (AddressID, ZipCode),
    FOREIGN KEY (ZipCode) REFERENCES ZipCode(ZipCode) ON DELETE RESTRICT -- Prevent deletion if there is at least one address with that Zip Code
);
CALL LogEvent('Address Table Creation');
CALL LogEvent('Address Table Created!');


-- Create the Department table
-- This table stores information about different departments in the company.
DROP TABLE IF EXISTS Department;
CREATE TABLE Department (
    DepartmentID INT PRIMARY KEY,
