    Location VARCHAR(100)
);
CALL LogEvent('Department Table Creation');
CALL LogEvent('Department Table Created!');


-- Create Employees table
-- This table stores information about employees, including their department, joining date, and salary.
DROP TABLE IF EXISTS Employee;
CREATE TABLE Employee (
    EmployeeID INT,
    FirstName VARCHAR(100),
    LastName VARCHAR(100),
    JoiningDate DATE,
    Salary DECIMAL(10,2), -- DECIMAL datatype with precision 10 and scale 2 for storing salaries with 2 decimal places
    DepartmentID INT,
    PRIMARY KEY (EmployeeID, DepartmentID),
    FOREIGN KEY (DepartmentID) REFERENCES Department(DepartmentID)
        ON DELETE RESTRICT -- Makes it impossible to delete any department with employees working on it
);


-- Create the Customers table with an identifying relationship
-- This table stores customer information, including their address.
DROP TABLE IF EXISTS Customer;
CREATE TABLE Customer (
    CustomerID INT,
    FirstName VARCHAR(100),
    LastName VARCHAR(100),
    Email VARCHAR(50),
    PhoneNumber VARCHAR(20),
    TaxpayerNumber INT UNIQUE,
    AddressID INT,
    PRIMARY KEY (CustomerID, AddressID),  -- Composite primary key
    FOREIGN KEY (AddressID) REFERENCES Address(AddressID) ON DELETE RESTRICT -- Prevent deletion of AddressID if there is at least one customer with that AddressID
);

CALL LogEvent('Customer Table Creation');
CALL LogEvent('Customer Table Created!');


