-- Create VideoGame table
-- This table stores information about video games, including their genre, publisher, and platform.
DROP TABLE IF EXISTS VideoGame;
CREATE TABLE VideoGame (
    VideoGameID INT,
    Title VARCHAR(100),
    Game_Description TEXT,
    ReleaseDate DATE,
    Price DECIMAL(10,2), -- DECIMAL datatype with precision 10 and scale 2 for storing prices with 2 decimal places
    OnlineMode INT,
    GenreID INT NULL,
    PublisherID INT,
    PlatformID INT,
    PRIMARY KEY (VideoGameID),  -- Composite primary key
    FOREIGN KEY (GenreID) REFERENCES Genre(GenreID) ON DELETE SET NULL, -- Set genre attribute to NULL if the corresponding genre is deleted
    FOREIGN KEY (PlatformID) REFERENCES Platform(PlatformID) ON DELETE SET NULL, -- Set platform attribute to NULL if the corresponding platform is deleted
    FOREIGN KEY (PublisherID) REFERENCES Publisher(PublisherID) ON DELETE SET NULL -- Set publisher attribute to NULL if the corresponding publisher is deleted
