CALL LogEvent('VideoGame Table Creation');
CALL LogEvent('VideoGame Table Created!');


-- Create Order table with an identifying relationship
-- This table stores information about orders, including the associated customer, employee, and video game.
DROP TABLE IF EXISTS `Order`;
CREATE TABLE `Order` (
    OrderID INT AUTO_INCREMENT,
    OrderDate DATETIME,
    ShipDate DATE,
    CustomerID INT,
    EmployeeID INT,
    VideoGameID INT,
    PRIMARY KEY (OrderID, CustomerID, EmployeeID, VideoGameID),  -- Composite primary key
    FOREIGN KEY (CustomerID) REFERENCES Customer(CustomerID) ON DELETE RESTRICT ON UPDATE CASCADE, -- RESTRICT deletion if there is at least one order involving the customer
    FOREIGN KEY (VideoGameID) REFERENCES VideoGame(VideoGameID) ON DELETE RESTRICT ON UPDATE CASCADE, -- RESTRICT deletion if there is at least one order involving the video game
    FOREIGN KEY (EmployeeID) REFERENCES Employee(EmployeeID) ON DELETE RESTRICT ON UPDATE CASCADE -- RESTRICT deletion if there is at least one order involving the employee
);

-- Create an index on the OrderDate attribute to improve query performance for operations involving this field
CREATE INDEX idx_orderdate ON `Order` (OrderDate);

CALL LogEvent('Order Table Creation');
CALL LogEvent('Order Table Created!');


-- Create OrderDetails table with an identifying relationship
-- This table stores information about order details, including the quantity, total amount, and associated order and video game.
