    TotalAmount DECIMAL(10,2), -- DECIMAL datatype with precision 10 and scale 2 for storing total amounts with 2 decimal places
    OrderID INT,
    PRIMARY KEY (OrderDetailID, OrderID),  -- Composite primary key
    FOREIGN KEY (OrderID) REFERENCES `Order`(OrderID) ON DELETE RESTRICT ON UPDATE CASCADE -- RESTRICT deletion if there is at least one order detail corresponding to the order and update if the correspondent order is updated
