-- Create Inventory table with an identifying relationship
-- This table stores information about inventory, including stock quantity, associated video game, and supplier order.
DROP TABLE IF EXISTS Inventory;
CREATE TABLE Inventory (
    StockQuantity INT,
    VideoGameID INT,
    SupplierOrderID INT,
    PRIMARY KEY (VideoGameID, SupplierOrderID),
    FOREIGN KEY (VideoGameID) REFERENCES VideoGame(VideoGameID) ON DELETE RESTRICT, -- Prevent deletion of associated video game
    FOREIGN KEY (SupplierOrderID) REFERENCES SupplierOrder(SupplierOrderID) ON DELETE RESTRICT -- Prevent deletion of associated supplier order
);

CALL LogEvent('Inventory Table Creation');
CALL LogEvent('Inventory Table Created!');


-- Create Rating table with an identifying relationship
-- This table stores information about ratings given by customers to video games.
DROP TABLE IF EXISTS Rating;
CREATE TABLE Rating (
    RatingID INT,
    Rating INT,
    ReviewText TEXT,
    RatingDate DATETIME,
    CustomerID INT,
    VideoGameID INT,
    PRIMARY KEY (RatingID),
    FOREIGN KEY (CustomerID) REFERENCES Customer(CustomerID) ON DELETE CASCADE ON UPDATE CASCADE, -- Delete all ratings given by deleted customers
    FOREIGN KEY (VideoGameID) REFERENCES VideoGame(VideoGameID) ON DELETE SET NULL ON UPDATE CASCADE -- Set VideoGame attribute to NULL if the corresponding video game is deleted
);

-- Creating an index on VideoGameID in the Rating table
CREATE INDEX idx_videogameid_rating ON Rating (VideoGameID);

-- Creating an index on CustomerID in the Rating table
CREATE INDEX idx_customerid_rating ON Rating (CustomerID);

CALL LogEvent('Rating Table Creation');
CALL LogEvent('Rating Table Created!');

-- Add a CHECK constraint to enforce the rating range (0 as minimum and 10 as maximum)
ALTER TABLE Rating
ADD CONSTRAINT chk_RatingRange CHECK (Rating >= 0 AND Rating <= 10);


