DELIMITER //

-- Create a BEFORE UPDATE trigger on the Inventory table
CREATE TRIGGER create_supplier_order
BEFORE UPDATE ON Inventory
FOR EACH ROW
BEGIN
    -- Check if StockQuantity is going to be set to 0
    IF NEW.StockQuantity <= 0 THEN
        -- Insert a new row into SupplierOrder
        -- This trigger is designed to automatically create a supplier order
        -- when the StockQuantity of an item in the Inventory table is updated to 0.
        -- It sets the OrderDate to the current date and the DeliveryDate to one year from the current date.
        -- The SupplierID is set to 5, and the Status is set to 'Waiting for delivering'.
        VALUES (NOW(), DATE_ADD(NOW(), INTERVAL 1 YEAR), 5, 'Waiting for delivering');
    ELSE
        -- If StockQuantity is not going to be 0, simply decrement StockQuantity by 1.
        SET NEW.StockQuantity = NEW.StockQuantity;
    END IF;
END //

-- Reset the delimiter back to semicolon
DELIMITER ;

