-- Set a different delimiter for the function creation
DELIMITER //


-- This function encapsulates the logic of creating an order and updating the inventory
-- It is defined to automate the order creation and inventory management
CREATE FUNCTION CreateOrder(p_VideoGameID INT, p_Quantity INT)
RETURNS INT
DETERMINISTIC
BEGIN
    -- Create a new order with given details
    VALUES (NOW(), DATE_ADD(NOW(), INTERVAL 1 YEAR), 5, 1, p_VideoGameID);
    
    -- Update the inventory by reducing the stock quantity
    -- Ensures inventory levels are adjusted as per the new order
    UPDATE Inventory
    SET StockQuantity = StockQuantity - p_Quantity
    WHERE VideoGameID = p_VideoGameID;
    
    -- Returns 0 as an indication of successful execution
    -- This function is more about performing actions rather than returning a value
    RETURN 0;
END //

-- Reset the delimiter back to semicolon
DELIMITER ;

